import React from "react";
import { Icon, Modal } from "web3uikit";
import { useState, useEffect } from "react";
import {useMoralis} from "react-moralis";

function User({account}) {

  const [isVisible, setVisible] = useState(false);
  const { Moralis } = useMoralis();
  const [useRentals, setUserRentals] = useState();

  useEffect(() => {
    async function fetchRentals() {
      const Rentals = Moralis.Object.extend("newBookings");
      const query = new Moralis.Query(Rentals);
      query.equalTo("booker", account);
      const result = await query.find();

      setUserRentals(result);
    }
    fetchRentals();
  }, [isVisible]);

  return (
    <>
      <div onClick={() => setVisible(true)}>
        <Icon fill="#000000" size={24} svg="user" />
      </div>

      <Modal
        onCloseButtonPressed={() => setVisible(false)}
        hasFooter = {false}
        title = "Your Cars" 
        isVisible = {isVisible}

      >

      </Modal>
    </>
  );
}

export default User;
